package com.zxy97.servlet;

import java.io.IOException;
import java.util.HashMap;

import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import com.zxy97.rsa.JsonUtil;
import com.zxy97.rsa.RSAUtils;

public class RsaServlet extends HttpServlet {

    private static final long serialVersionUID = 1L;

    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        doPost(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {
        // 获取数据
        String uname = request.getParameter("uname");
        String password = request.getParameter("password");
        //String privateKey = request.getParameter("privateKey");
        String privateKey = "";
        // 解密
        uname = RSAUtils.decryptDataOnJava(uname, privateKey);
        password = RSAUtils.decryptDataOnJava(password, privateKey);
        // 用map封装返回的数据
        HashMap<String, Object> result = new HashMap<String, Object>();
        result.put("uname", uname);
        result.put("password", password);
        
        response.getWriter().print(JsonUtil.toJson(result));

    }

}
